select NVL(avg(N_VALUE),0) from ky_gy_db where 1=1 and S_PARA_NAME = '真空度' and S_STAGE_NAME = '浸膏冷藏'
and S_BATCH_NUMBER = #{batchNumber}
and TM_CURT >= to_date(#{start}, 'yyyy-mm-dd hh24:mi:ss') and TM_CURT <= to_date(#{end}, 'yyyy-mm-dd hh24:mi:ss')

${pager.oracleQueryConditionStart}
SELECT a.S_BATCH_NUMBER, round(AVG(a.jynsyl),3) as jynsyl, round(AVG(a.jynszkd),3) as jynszkd , round(AVG(a.qgnczkd),3) as qgnczkd FROM ( SELECT S_BATCH_NUMBER AS S_BATCH_NUMBER , DECODE(S_PARA_NAME, '蒸汽压力', N_VALUE, 0) AS jynsyl , DECODE(S_PARA_NAME, '真空度', N_VALUE, 0) AS jynszkd , DECODE(S_STAGE_NAME, '浸膏冷藏', N_VALUE, 0) AS qgnczkd FROM ky_gy_db WHERE (1 = 1 AND ((S_PARA_NAME IN ('蒸汽压力', '真空度') AND S_STAGE_NAME = '减压浓缩') OR (S_STAGE_NAME = '浸膏冷藏' AND S_PARA_NAME = '真空度'))
and S_BATCH_NUMBER = #{batchNumber}
and TM_CURT >= to_date(#{start}, 'yyyy-mm-dd hh24:mi:ss') and TM_CURT <= to_date(#{end}, 'yyyy-mm-dd hh24:mi:ss'))
order by tm_curt desc
) a GROUP BY a.S_BATCH_NUMBER
${pager.orderCondition}
${pager.oracleQueryConditionEnd}

SELECT a.S_BATCH_NUMBER, round(AVG(a.jynsyl),3) as jynsyl, round(AVG(a.jynszkd),3) as jynszkd , round(AVG(a.qgnczkd),3) as qgnczkd FROM ( SELECT S_BATCH_NUMBER AS S_BATCH_NUMBER , DECODE(S_PARA_NAME, '蒸汽压力', N_VALUE, 0) AS jynsyl , DECODE(S_PARA_NAME, '真空度', N_VALUE, 0) AS jynszkd , DECODE(S_STAGE_NAME, '浸膏冷藏', N_VALUE, 0) AS qgnczkd FROM ky_gy_db WHERE (1 = 1 AND ((S_PARA_NAME IN ('蒸汽压力', '真空度') AND S_STAGE_NAME = '减压浓缩') OR (S_STAGE_NAME = '浸膏冷藏' AND S_PARA_NAME = '真空度'))
and S_BATCH_NUMBER = #{batchNumber}
and TM_CURT >= to_date(#{start}, 'yyyy-mm-dd hh24:mi:ss') and TM_CURT <= to_date(#{end}, 'yyyy-mm-dd hh24:mi:ss'))
order by tm_curt desc
) a GROUP BY a.S_BATCH_NUMBER
